client_script:
frappe.web_form.on('first_name', (field, value) => {
    first = frappe.web_form.get_value(['first_name']);
    last = frappe.web_form.get_value(['last_name']);
    frappe.web_form.set_value('full_name', strip(first + " " + last))
});

frappe.web_form.on('last_name', (field, value) => {
    first = frappe.web_form.get_value(['first_name']);
    last = frappe.web_form.get_value(['last_name']);
    frappe.web_form.set_value('full_name', strip(first + " " + last))
});

// frappe.web_form.set_value('current_salary', "&nbsp;")
// frappe.web_form.set_value('expected_salary', "&nbsp;")


// set validation for mobile (10 digits & numbers only) and pincode (6 digits & numbers only)

frappe.web_form.validate = () => {
    form_data = frappe.web_form.get_values();
    if (form_data.mobile.length != 10 || isNaN(form_data.mobile)) {
        frappe.msgprint('Invalid Mobile Number');
        return false;
    }

    else if (form_data.pincode.length != 6 || isNaN(form_data.pincode)) {
        frappe.msgprint('Invalid PIN Code');
        return false;
    }

    else if (isNaN(form_data.current_salary) || form_data.current_salary < 50000) {
        frappe.msgprint('Please enter your current annual salary in lakh. Eg, 5,50,000');
        return false;
    }


    else if (form_data.expected_salary === 0 || isNaN(form_data.expected_salary) || form_data.current_salary < 50000) {
        frappe.msgprint('Please enter your expected annual salary in lakh. Eg, 6,50,000');
        return false;
    }

return true;
};


// open success page on form submit
frappe.web_form.handle_success = () => window.location = frappe.web_form.success_url

